icon_record_locked.script:
function onInit()
	local node = window.getDatabaseNode();
	local bIsInHand = CardsManager.isCardInHand(node);
	local bIsInStorage = CardStorage.isCardInStorage(node);
	setVisible(bIsInHand or bIsInStorage);

	if bIsInHand then
		DB.setValue(node, "locked", "number", 1);
	end
end

button_record_locked.script:
function onInit()
	local node = window.getDatabaseNode();
	local bIsInHand = CardsManager.isCardInHand(node);
	local bIsInStorage = CardStorage.isCardInStorage(node);
	setVisible(not (bIsInHand or bIsInStorage));

	if super and super.onInit then
		super.onInit();
	end
end
