tokenfield.script:
function onInit()
	registerMenuItem(Interface.getString("deckbox_menu_play_top_card"), "play_faceup", 2);
	registerMenuItem(Interface.getString("deckbox_menu_view_deck_cards"), "deck", 3);
	registerMenuItem(Interface.getString("deckbox_menu_view_discard_cards"), "discard", 4);
end
function onMenuSelection(selection)
	if not Session.IsHost then
		return;
	end
	if selection == 2 then
		onDoubleClick();
	elseif selection == 3 then
		DesktopManager.openCardList(DeckManager.getCardsNode(window.getDatabaseNode()));
	elseif selection == 4 then
		DesktopManager.openCardList(DeckManager.getDiscardNode(window.getDatabaseNode()));
	end
end
function onDragStart(button, x, y, draginfo)
	local node = window.getDatabaseNode();
	if Session.IsHost then
		CardManager.onDragFromDeck(window.getDatabaseNode(), draginfo);
		return true;
	end
end
function onDrop(x, y, draginfo)
	CardManager.onDropCard(draginfo, window.getDatabaseNode(), DeckManager.DECK_CARDS_PATH);
end
function onDoubleClick(x, y)
	local aCards = DeckManager.getRandomCardsInDeck(window.getDatabaseNode(), 1);
	if aCards and aCards[1] then
		CardManager.playCard(aCards[1], DeckedOutUtilities.getFacedownHotkey(), true, {});
	end
end
